import React, { useState } from "react";
import "animate.css";
import RoomResult from "../common/RoomResult";
import RoomSearch from "../common/RoomSearch";

const HomePage = () => {
  const [roomSearchResults, setRoomSearchResults] = useState([]);

  const handleSearchResult = (results) => {
    setRoomSearchResults(results);
  };

  return (
    <div className="home">
      {/* HEADER SECTION */}
      <header className="header-banner animate__animated animate__fadeIn">
        <img
          src="./assets/images/hotel.webp"
          alt="Phegon Hotel"
          className="header-image"
        />
        <div className="overlay"></div>
        <div className="animated-texts overlay-content">
          <h1 className="animate__animated animate__fadeInDown">
            Welcome to <span className="phegon-color">VITU HOTEL</span>
          </h1>
          <h3 className="animate__animated animate__fadeInUp animate__delay-1s">
            Step into a haven of comfort and care
          </h3>
        </div>
      </header>

      {/* SEARCH SECTION */}
      <section className="animate__animated animate__fadeInUp">
        <RoomSearch handleSearchResult={handleSearchResult} />
        <RoomResult roomSearchResults={roomSearchResults} />
        <h4>
          <a
            className="view-rooms-home btn btn-primary animate__animated animate__bounceIn animate__delay-2s"
            href="/rooms"
          >
            View All Rooms
          </a>
        </h4>
      </section>

      {/* SERVICES SECTION */}
      <section className="service-section">
        <h2 className="home-services animate__animated animate__fadeInLeft">
          Services at <span className="phegon-color">VITU HOTEL</span>
        </h2>
        <div className="service-card-group">
          {[
            {
              img: "./assets/images/ac.png",
              title: "Air Conditioning",
              description:
                "Stay cool and comfortable throughout your stay with our individually controlled in-room air conditioning.",
            },
            {
              img: "./assets/images/mini-bar.png",
              title: "Mini Bar",
              description:
                "Enjoy a convenient selection of beverages and snacks stocked in your room's mini bar with no additional cost.",
            },
            {
              img: "./assets/images/parking.png",
              title: "Parking",
              description:
                "We offer on-site parking for your convenience. Please inquire about valet parking options if available.",
            },
            {
              img: "./assets/images/wifi.png",
              title: "WiFi",
              description:
                "Stay connected throughout your stay with complimentary high-speed Wi-Fi access available in all guest rooms and public areas.",
            },
          ].map((service, index) => (
            <div
              key={index}
              className={`service-card animate__animated animate__zoomIn animate__delay-${index}s`}
            >
              <img src={service.img} alt={service.title} />
              <div className="service-details">
                <h3 className="service-title">{service.title}</h3>
                <p className="service-description">{service.description}</p>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
